wrapper -->            <!-- Main Content Row -->            <div class="row">                <!-- Left Side (Product View) -->                <div class="col-xs-12 col-lg-3 main-content-body">                    <?php foreach($products as $product){ ?>                        <div class="row product-widget">                            <div class="col-lg-12">                                <div class="row">                                    <div class="col-lg-12">                                        <?php echo $product['name']; ?>                                    </div>                                </div>                                <div class="row">                                    <div class="col-lg-12">                                        <?php echo SITE_CURRENCY_PREFIX .' '.$product['price']; ?>                                    </div>                                </div>                                <div class="row">                                    <div class="col-lg-12">                                        <button class="btn btn-sm btn-primary" id='<?php echo $product['name']; ?>LoadingButton' onclick="addToCart('<?php echo $product['name'] ?>');"><i class="fa fa-plus"></i>&nbsp;&nbsp;Add to Cart</button>                                    </div>                                </div>                            </div>                        </div>                    <?php } ?>                </div>                <!-- Right Side (Cart View) -->                <div class="col-xs-12 col-lg-4 col-lg-offset-5 main-cart-body">                    <!-- start of cart accordion -->                    <div class="panel-group side-cart" id="accordion">                        <div class="panel panel-default">                            <div>                                <h2>                                    <a data-toggle="collapse" data-parent="#accordion" href="#collapseCart" style="text-decoration: 