                                        <?php } ?>                                                <tr><td>Total: </td><td colspan="3" style="text-align:right;"><?php echo SITE_CURRENCY_PREFIX .' '. number_format($_SESSION['cart_total'], 2); ?></td><td></td></tr>                                            </tbody>                                        </table>                                    <?php }else{ ?>                                        The cart is empty                                    <?php } ?>                                    <br/><br/>                                    <button class="btn btn-small" onclick="clearCart();">Clear Cart</button>                                </div>                            </div>                        </div>                    </div>                    <!-- End of cart accordion -->                </div>            </div>        </div> <!-- End of page wrapper -->        <!-- Cart Modal --><!--        <div class="modal fade" id="cartModal">--><!--            <div class="modal-dialog">--><!--                <div class="modal-content">--><!--                    <div class="modal-header">--><!--                        <button type="button" class="close" data-dismiss="modal"><span aria-hidden="true">&times;</span><span class="sr-only">Close</span></button>--><!--                        <h4 class="modal-title"><i class="fa fa-shopping-cart">Cart Details</h4>--><!--                    </div>--><!--                    <div class="modal-body">--><!--                        <p>One fine body&hellip;</p>--><!--                    </div>--><!--                    <div class="modal-footer">--><!--                        <button type="button" class="btn btn-default" data-dismiss="modal">Close</button>--><!--                        <button type="button" class="btn btn-primary">Save changes</button>--><!--                    </div>--><!--                </div><!-- /.modal-content -->--><!--            </div><!-- /.modal-dialog -->--><!--        </div><!-- /.modal -->-->        <!-- Post page load javascript -->        <script type="text/javascript">            /** This function clears a modal message span             *             * @param string parent     The id of the element to clear from messages             * */            function clearMessages(parent){                $("#"+parent).html("");            }            /** This function converts a div into a loading div             *             * @param string parent         The id of the element to convert into a loading element             * */            function enableLoadingDiv(parent){                $("#"+parent).html("<div class='col-lg-12' style='text-align: center;'><i class='fa fa-cog fa-5x fa-spin text-info'></i></div>");            }            /** This function changes the innerHTML of a button to the loading text, it also disables the button             *             * @param string button         The id of the button to set to a loading button             * */            function setLoadingButton(button){                $("#"+button).prop("disabled", "disabled");                $("#"+button).html("<i class='fa fa-cog fa-spin'></i> Loading...");            }            /** This function changes the innerHTML of a button from a loading button to normal button, also enables the button             *             * @param string button         The id of the button to be edited             * @param string html           The html of the button to be changed to             * */            function changeBackLoadingButton(button, html){                $("#"+button).prop("disabled", "");                $("#"+button).html(html);            }            /** Display a message on a modal window             *             * @param string type       Type of message to display using theme helper class (success, info, danger, etc.)             * @param string parent     The id of the parent element in which to display the message             * @param string message    The html message to display in the message wrapper             * */            function displayMessage(parent, type, message){                /** Create message and append to parent */                var message = "<div class='row'><div class='col-lg-12'><div class='alert alert-"+ type +"'>"+ message +"</div></div></div>";                $("#"+parent).html(message);                /** Scroll to message */                $([document.documentElement, document.body]).animate({                    scrollTop: $("#"+parent).offset().top                }, 500);            }            function addToCart(item){                /** Clear messages */                clearMessages('main-message-bar');                /** Add and prepare post data */                var post_data = new Object();                post_data.action = 'addItem';                post_data.item = item;                /** Execute Ajax call to add item to cart */                $.ajax({                    url: "<?php echo $websiteURLRoot; ?>ajax/editCart.php",                    type: "POST",                    data: post_data,                    dataType: "json",                    beforeSend: function (data) {                        /** Set loading button and disable while operation is busy */                        setLoadingButton(item+"LoadingButton");                    },                    success: function(data){                        /** Handle if success, else display error message */                        if(data.message_type == "success"){                            if(data.payload.cart){                                /** Update the cart div with new values */                                $("#cart_content_div").html("<table class='table' id='cart_table'>" +                                    "<thead>" +                                        "<tr>" +                                            "<th>Product</th>" +                                            "<th>Price</th>" +                                            "<th>Quantity</th>" +                                            "<th>Total</th>" +                                            "<th>Remove</th>" +                                        "</tr>" +                                    "</thead>" +                                    "<tbody id='cart_body'></tbody>" +                                "</table>");                                for(var x = 0; x < data.payload.cart.length; x++){                                    /** Add items to table body */                                    $("#cart_body").append("" +                                        "<tr>" +                                            "<td>"+ data.payload.cart[x].name +"</td>" +                                            "<td>"+ currency_prefix +" "+data.payload.cart[x].price.toFixed(2) +"</td>" +                                            "<td>"+ data.payload.cart[x].quantity +"</td>" +                                            "<td>"+ currency_prefix +" "+(data.payload.cart[x].price * data.payload.cart[x].quantity).toFixed(2) +"</td>" +                                            "<td><i class='fa fa-remove'></i></td>"+                                        "</tr>");                                }                                /** Append Total Row */                                $("#cart_body").append("<tr><td>Total: </td><td colspan='3' style='text-align:right;'>"+ currency_prefix +" "+data.payload.total.toFixed(2) +"</td><td></td></tr>");                                /** Append clear cart button */                                $("#cart_content_div").append("<br/><br/><button class='btn btn-small' onclick='clearCart();'>Clear Cart</button>");                            }                        }else{                            displayMessage('main-message-bar', data.message_type, data.message_content);                        }                    },                    error: function(data){                        /** Handle Return Data */                        var return_data = null;                        if(data.responseText){                            return_data = JSON.parse(data.responseText);                            displayMessage("main-message-bar", return_data.message_type, return_data.message_content);                        }else{                            displayMessage("main-message-bar", 'danger', 'Unknown error occurred. See log for details.');                        }                    },                    complete: function(data){                        /** Change back loading button text and enable button again */                        changeBackLoadingButton(item+"LoadingButton", "<i class='fa fa-plus'></i>&nbsp;&nbsp;Add to Cart");                    }                });            }            function clearCart(){                /** Clear messages */                clearMessages('main-message-bar');                /** Add and prepare post data */                var post_data = new Object();                post_data.action = 'clearCart';                /** Execute Ajax call to add item to cart */                $.ajax({                    url: "<?php echo $websiteURLRoot; ?>ajax/editCart.php",                    type: "POST",                    data: post_data,                    dataType: "json",                    beforeSend: function (data) {                        /** Set loading button and disable while operation is busy */                        //setLoadingButton(item+"LoadingButton");                    },                    success: function(data){                        /** Handle if success, else display error message */                        if(data.message_type == "success"){                            /** Update the cart div with new values */                            if(!data.payload){                                /** Remove the cart table */                                $("#cart_table").remove();                                $("#cart_content_div").html("The cart is empty");                            }                        }else{                            displayMessage('main-message-bar', data.message_type, data.message_content);                        }                    },                    error: function(data){                        /** Handle Return Data */                        var return_data = null;                        if(data.responseText){                            return_data = JSON.parse(data.responseText);                            displayMessage("main-message-bar", return_data.message_type, return_data.message_content);                        }else{                            displayMessage("main-message-bar", 'danger', 'Unknown error occurred. See log for details.');                        }                    },                    complete: function(data){                        /** Change back loading button text and enable button again */                        //changeBackLoadingButton(item+"LoadingButton", "<i class='fa fa-plus'></i>&nbsp;&nbsp;Add to Cart");                    }                });            }        </script>    </body></html>